function lhb_drawHeader() {
    if (!lhb_handleCanada()) {
        lhb_insertStyleSheets();
        document.write(unescape(lhb_getEscapedHeaderHTML()));
        lhb_activateQuickMenu();
    }
}

function lhb_handleCanada() {
    if (GeoIP["COUNTRY_CODE"] && (GeoIP["COUNTRY_CODE"] == "CA")) {
        if (!lhb_readCookie("NoCanadaRedirect")) {
            if (location.href == "http://search.lycos.com/default.asp?query=travel&loc=travel") {
                top.location = "http://www.lycos.ca/travel/";
            }
            var caRedirects=[[/^multimedia\.lycos\.com(:\d+)?$/i,
                              "http://www.lycos.ca/entertainment/"],
                             [/^((realestate\.lycos\.com)|(livingchoices\.com))(:\d+)?$/i,
                              "http://www.lycos.ca/home-garden/real-estate.html"],
                             [/^(shop(ping)?|bizrate)\.lycos\.com(:\d+)?$/i,
                              "http://www.lycos.ca/shopping/"],
                             [/^travel\.lycos\.com(:\d+)?$/i,
                              "http://www.lycos.ca/travel/"],
                             [/^entertainment\.lycos\.com(:\d+)?$/i,
                              "http://www.lycos.ca/entertainment/"],
                             [/^tv\.lycos\.com(:\d+)?$/i,
                              "http://www.lycos.ca/entertainment/television.html"],
                             [/^video\.lycos\.com(:\d+)?$/i,
                              "http://www.lycos.ca/entertainment/music-videos.html"],
                             [/^music\.lycos\.com(:\d+)?$/i,
                              "http://www.lycos.ca/entertainment/music.html"],
                             [/^(www\.)?lycos\.com(:\d+)?$/i,
                              "http://www.lycos.ca"]];
            for (var i=0; i<caRedirects.length; i++) {
                if (location.host.match(caRedirects[i][0])) {
                    top.location = caRedirects[i][1];
                    return true;
                }
            }
        }

        var caHeaders=[[/angelfire\.lycos\.com(:\d+)?$/i,       "angelfire"],
                       [/gamesville(\.lycos)?\.com(:\d+)?$/i,   "gamesville"],
                       [/hotbot(\.lycos)?\.com(:\d+)?$/i,       "hotbot"],
                       [/tripod\.lycos\.com(:\d+)?$/i,          "tripod"],
                       [/wired(news)?(\.lycos)?\.com(:\d+)?$/i, "wired"],
                       [/webmonkey(\.wired)?\.com(:\d+)?$/i,    "webmonkey"],
                       [/(lycos)?mail(\.lycos)\?.com(:\d+)?$/i, "lycosmail"]];

        for (var i=0; i<caHeaders.length; i++) {
            if (location.host.match(caHeaders[i][0])) {
                document.write("<iframe marginheight=\"0\" marginwidth=\"0\" "+
                               "scrolling=\"no\" frameborder=\"0\" "+
                               "width=\"100%\" height=\"100\" "+
                               "src=\"http://www.lycos.ca/header-"+
                               caHeaders[i][1]+"/\"></iframe>\n");
                return true;
            }
        }
    }
    return false;
}

