function lhb_insertStylesheet(style_uri) {
    if (document.createStyleSheet) {
        this.document.createStyleSheet(style_uri);
    } else {
        var link = document.createElement("link");
        link.rel = "stylesheet";
        link.type = "text/css";
        link.href = style_uri;
        link.media = "screen";
        document.getElementsByTagName('head')[0].appendChild(link);
    }
}

function lhb_insertScript(url) {
    var script = document.createElement("script");
    script.setAttribute("type","text/javascript");
    script.setAttribute("src", url);
    document.getElementsByTagName("head")[0].appendChild(script);
}


function lhb_contains(container, containee) {
    while (containee != null) {
        if (container == containee) return true;
        containee = containee.parentNode;
    }
    return false;
}

function lhb_checkMouseEnter(element, evt) {
    if (element.contains && evt.fromElement) {
        return !element.contains(evt.fromElement);
    } else if (evt.relatedTarget) {
        return !lhb_contains(element, evt.relatedTarget);
    }
    return false;
}

function lhb_checkMouseLeave(element, evt) {
    if (element.contains && evt.toElement) {
        return !element.contains(evt.toElement);
    } else if (evt.relatedTarget) {
        return !lhb_contains(element, evt.relatedTarget);
    }
    return false;
}

function lhb_enterQuickMenu(e) {
    if (!e) var e = window.event;
    if (lhb_checkMouseEnter(this, e)) {
	lhb_openQuickMenu(e);
    }
}

function lhb_openQuickMenu(e){
    if (!e) var e = window.event;
    var popLnk = document.getElementById("popLnk");
    var mnuBx  = document.getElementById("mnuBx");
    if (popLnk && mnuBx) {
        var targetWidth  = 454;
        var targetHeight = 50;
        var targetLeft = 40 - targetWidth/2;
        var targetTop = 15;

        if (popLnk.offsetParent) {
            var obj = popLnk;
            while (obj.offsetParent) {
                targetLeft += obj.offsetLeft;
                targetTop  += obj.offsetTop;
                obj = obj.offsetParent;
            }
        } else if (popLnk.x) {
            targetLeft += obj.x;
            targetTop  += obj.y;
        }

        mnuBx.style.left    = (targetLeft   + "px");
        mnuBx.style.top     = (targetTop    + "px");
        mnuBx.style.width   = (targetWidth  + "px");
        mnuBx.style.height  = (targetHeight + "px");
        mnuBx.style.display = "block";
    }
}

function lhb_leaveQuickMenu(e) {
    if (!e) var e = window.event;
    if (lhb_checkMouseLeave(this, e)) {
        var mnuBx = document.getElementById("mnuBx");
        if (mnuBx) {
            mnuBx.style.display = "none";
        }
    }
}

function lhb_activateQuickMenu() {
    var popLnk = document.getElementById("popLnk");
    if (popLnk) {
        if (popLnk.addEventListener) {
            popLnk.addEventListener('click', lhb_openQuickMenu, false);
        } else {
            popLnk.onclick = lhb_openQuickMenu;
        }
    }

    var mnuBx = document.getElementById("mnuBx");
    if (mnuBx) {
        if (mnuBx.addEventListener) {
            mnuBx.addEventListener('mouseout', lhb_leaveQuickMenu, false);
        } else {
            mnuBx.onmouseout = lhb_leaveQuickMenu;
        }
    }
}



